      for(j=0;j<=sum;j++)
      {
        dp[i][j]=max(dp[i-1][j],dp[i-1][j+h[i]]);
        if(j>=h[i]) dp[i][j]=max(dp[i][j],dp[i-1][j-h[i]]+h[i]);
        else dp[i][j]=max(dp[i][j],dp[i-1][h[i]-j]+j);
      }
    if(dp[n][0])  cout<<dp[n][0]<<endl;
