    if(sqrt((x2-x[i])*(x2-x[i])+(y2-y[i])*(y2-y[i]))<r[i]&&sqrt((x1-x[i])*(x1-x[i])+(y3-y[i])*(y3-y[i]))>r[i])time++;     (同上，如果没有这一句会漏解）
    }
    cout<<time;
    return 0;
