	node operator + (node a){
		register node c;
		register int x=0,i;
		c.len=max(this->len,a.len);
		for(i=1;i<=c.len;i++){
			c.num[i]=a.num[i]+this->num[i]+x;
			x=c.num[i]/10;c.num[i]%=10;
		}
