		return c;
	}
    //重定义+，让它可以进行高精度加法
	node operator * (int a){
		register node c=*this;
		register int x=0,i;
		for(i=1;i<=c.len;i++){
			c.num[i]=c.num[i]*a+x;
			x=c.num[i]/10;c.num[i]%=10;
