	if(a[1]!=1)
	{
		printf("-1");
		return 0;
	}                               //如果面值中没有1，则凑不出1元，无解；如果有1元，则可凑出任意面值 
	while(sum<x)
	{
	    for(i=n;i>=1;i--)           //从大到小循环，贪心，面值越大越好 
	        if(a[i]<=sum+1)break;   //sum是已经凑出的硬币总合，在1~sum+1中选出最大的可用硬币 
	    ans++;
	    sum+=a[i];                  //累加 
	}
	printf("%d",ans);               //输出 
	return 0;
