		return 0;
	}
	int ans=0,sum=0;
	for(int i=1;i<=x;i++){
		if(i>sum){
			for(int j=n;j>=1;j--){	//每次要选最大面额
				if(a[j]<=i){ 
					sum+=a[j];
					ans++;
