}
int check(){
    memset(dp,0x3f,sizeof(dp));
    dp[0] = 0,dp[1] = 1;
    for(int i = 1;i <= x*2;i ++){
        for(int j = 1;j <= n;j ++){
            if(i - v[j] >= 0 && v[j] <= i - v[j] + 1)
                dp[i] = min(dp[i],dp[i - v[j]] + 1);
        }
        if(i >= x)ans = min(ans,dp[i]);
    }
