int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;++i)
		scanf("%d%d",&x[i],&y[i]),x[i]=(x[i]+1000)<<1,y[i]=(y[i]+1000)<<1,map[x[i]][y[i]]=1;
