int vis[maxn][maxn], x[maxn], y[maxn], r[maxn];

inline int Dist(int a, int b, int c, int d) {
  return (c - a) * (c - a) + (d - b) * (d - b);
}

inline bool Cmp(int a, int b) {  
  if( x[a] != x[b] ) return x[a] < x[b];
  return y[a] > y[b];
