    r[i] = i, x[i] += 200, y[i] += 200, vis[x[i]][y[i]] = 1;
  }
  sort(r + 1, r + n + 1, Cmp);
  for(int i = 1; i <= n; ++i) {
    for(int j = i + 1; j <= n; ++j) {
      int dx = x[r[i]] - x[r[j]], dy = y[r[i]] - y[r[j]];
      for(int k = j + 1; k <= n; ++k) {
