void dfs(int x)
{
	if(x<16)
	{
		sum+=x;
		minn=min(sum,minn);
		sum-=x;
		return;
	}
	if(x<81)
	{
		int b=x/16;
		sum+=b+x%16;
		minn=min(sum,minn);
		sum-=b+x%16;
		return;
