	if(sum>=minn) return;
	for(int i=a;i>=1;i--)
	{
		int b=x-four[i];
		sum++;
		if(sum+x/four[i]>minn) //可行性剪枝
		{
			sum--;
			return;
		}
		if(b>0) dfs(b);
		if(b==0)
			minn=min(minn,sum);
		sum--;
	}
}
int main()//从此开始阅读
{
	scanf("%d",&m);
	for(int i=1;;i++)//预处理
