int main(){
	n=read();
	char c[18];
	for(int i=1;i<=n;i++)
	{
		scanf("%s",c);
		a[i]=c;
	}
	inser(a[n],n);
	for(int iw=n-1;iw>=1;iw--)
	{
		cst++;
		ooo=1;
		string w=a[iw];
		int o=a[iw].size();
		for(int i=0;i<o;i++)
