			{
				add(iw,find2(w,i,j));
			}
		}
		for(int i=0;i<o;i++)
		{
			for(int j=0;j<26;j++)
			{
				add(iw,find3(w,i,j));
			}
		}
		inser(a[iw],iw);
	}
	queue<int>q;
	for(int i=1;i<=n;i++)
	{
		if(!rd[i])
		{
			q.push(i);
		}
	}
	while(!q.empty())
	{
		int f=q.front();
		q.pop();
		longest[f]++;
		for(int i=h[f];i;i=ew[i].next)
		{
			rd[ew[i].to]--;
			if(!rd[ew[i].to])q.push(ew[i].to);
			longest[ew[i].to]=max(longest[ew[i].to],longest[f]);
		}
	}
	int maxx=0;
	for(int i=1;i<=n;i++)
