```cpp
#include<bits/stdc++.h>
using namespace std;
class problem{
	private:
		string h;
	public:
		problem readline(){
			getline(cin,h);
			return *this;
		}
		problem flitter(char c){
			string w;
			for(int i=0;i<h.length();++i)if(h[i]!=c)w.push_back(h[i]);
			h=w;
			return *this;
		}
		int getans(){
		    int flg=0;
			int a1,a2;
			for(int i=0;i<h.size();++i){
				if(h[i]=='+')flg=1;
				if(h[i]=='-')flg=2;
				if(h[i]=='='){
					sscanf(h.c_str(),flg==1?"%d+%d=?":"%d-%d=?",&a1,&a2);
					return flg==1?a1+a2:a1-a2;
				}
				if(h[i]=='?'){
				    char f;
				    if(flg){
				    	sscanf(h.c_str(),flg==1?"%d+?=%d":"%d-?=%d",&a1,&a2);
				    	return flg==1?a2-a1:a1-a2;
					}
					else{
						sscanf(h.c_str(),"?%c%d=%d",&f,&a1,&a2);
						if(f=='+')return a2-a1;
				    	else return a1+a2;
					}
				}
			}
		}
}P1689; 
int main(){
	printf("%d\n",P1689.readline().flitter(' ').getans());
	return 0;
}
