#include<iostream>
#include<cstdio> 
using namespace std;
string str,S;
bool flag=1;//判断此时读入的数字是a还是b
long long a;
long long b;
long long i;
int type;//算式的类型：1:?+...||...+? 2:?-...||...-? 3:...+...=?||...-...=?
int unkownPos=-1;//未知数的位置
char ssd;//运算符
int o;//运算符的位置
long long Makenum(int begin){//在str里从begin开始读取数字
    i=begin;
    long long ret=0;
    while(str[i]>='0'&&str[i]<='9'&&i<str.length()){
        ret=ret*10+str[i]-'0';
        i++;
    }
    return ret;
}
int main()
{
	while(cin>>S)str+=S;//骚操作
	for(i=0;i<=str.length()-1;++i)
	{
		if(str[i]>='0'&&str[i]<='9'){
			if(flag==1){
				a=Makenum(i);//对a、b进行赋值
				flag=0;
			}
			else b=Makenum(i);
		}
		if(str[i]=='+'){
			type=1;//类型定为1
			o=i;
			ssd='+';
		}
		if(str[i]=='-'){
			type=2;//类型定为2
			o=i;
			ssd='-';
		}
		if(str[i]=='='){
			for(int j=i;j<str.length();++j){
				if(str[j]=='?')type=3;//寻找等号后面是否存在未知数，如果有，类型定为3
			}
		}
		if(str[i]=='?')unkownPos=i;//定位未知数
	}
