			}
			else if(unkownPos<o&&unkownPos!=-1){//如果未知数在运算符前面
				ans=a+b;
			}
		}
		if(type==3){//类型为3时直接做计算
			if(ssd=='+')ans=a+b;
			if(ssd=='-')ans=a-b;
		}
	}
