位运算优化（如果你拿去交会WA一个点因为存不下！！！）

```cpp
#include<iostream>
#include<cstdio>
#include<climits>
#include<algorithm>
#include<cstring>
#include<queue>
using namespace std;
#define ull unsigned long long
int read(){
    int q=0;char ch=' ';
    while(ch<'0'||ch>'9')ch=getchar();
    while(ch>='0'&&ch<='9')q=q*10+ch-'0',ch=getchar();
    return q;
}
ull ma[105],s[105];
int b[105],c[105];int ans,n,m;
void dfs(int x,int num,ull zt){//当前枚举位置，选择人数，当前选择的状态
    if(x==n+1){
        if(num>ans){//更新状态
            ans=num;
            for(int i=1;i<=n;i++)c[i]=b[i];
        }
        return;
    }
    if(num+n-x+1<ans)return;//剪枝
    ull kl=zt&ma[x];//位运算判断
    if(!kl){b[x]=1;dfs(x+1,num+1,zt|s[x]);b[x]=0;}
    dfs(x+1,num,zt);
}
int main()
{
    int i,j,x,y;
    n=read();m=read();
    s[1]=1;for(i=2;i<=n;i++)s[i]=s[i-1]*2;
    for(i=1;i<=m;i++){
        x=read();y=read();
        ma[x]|=s[y];ma[y]|=s[x];//用位运算保存关系
    }
    dfs(1,0,0);
    printf("%d\n",ans);
    for(i=1;i<=n;i++)printf("%d ",c[i]);
    return 0;
}
```

