using namespace std;
int n,m,ans=0,c[101],tmp[101]={0},b[101]={0};//c为 
bool a[101][101]={0};
void search(int t,int s)//第t个人 卫队中有s个人
{
    if(t==n+1)//n个人都搜素了 
    {
        if(s>ans)//如果更优 
        {
            ans=s;
            for(int i=1;i<=n;i++)
                c[i]=tmp[i];//保存tmp[],s 
        }
        return ;
    }
    if(b[t]==0)//如果可以选 
        for(int i=1;i>=0;i--)
        {
            if(i==1)//1代表选 
            {
                tmp[t]=1;
                s++;
                b[t]++;//重点：仇恨不知1,所以要++ 
                for(int j=1;j<=n;j++)
                    if(a[t][j])
                        b[j]++;
                search(t+1,s);
                tmp[t]=0;//回溯 
                s--;
                b[t]--;
                for(int j=1;j<=n;j++)
                    if(a[t][j])
                        b[j]--;
            }
            else
                search(t+1,s);
        }
    else
        search(t+1,s);
}
int main()
{
    freopen("1.txt","r",stdin);
    scanf("%d %d",&n,&m);
    for(int i=1;i<=m;i++)
