		scanf("%d",&a[i]);
	memset(c,0,sizeof(c));
	c[0][0]=1;//直接在main中预处理了，懒~~~
	for(int i=1;i<=m;i++){
		c[i][0]=1;
		for(int j=1;j<=i;j++)
			c[i][j]=c[i-1][j]+c[i-1][j-1];
