```cpp
var
n:integer;
a,f:array[1..1000]of integer;
Procedure dfs(k:integer);
var
i,j:integer;
begin
  if k>n then // 当k>n的时候，就是到了这一行数的边缘，所以输出这一行数
  begin
    for i:=1 to n do write(a[i]:5);  //注意输出格式
    writeln;
  end
  else
  for j:=1 to n do
  begin
    if f[j]=0 then //f[j]=0，说明这一行还没有数字
    begin
      a[k]:=j;  //没有数字，就把当前这个填上
      f[j]:=1;  //于是这行就被占用了
      dfs(k+1);  //考虑下一个数字
      f[j]:=0;  //因为每一层之后要把下一个数字作为起点那个，所以回归每一行都是0
    end;
  end;
end;
begin
  read(n);
  dfs(1);
end.
