```cpp
//P1706 全排列问题
//写一个非递归的全列排问题
#include<iostream>
#include<iomanip>//setw(5)的头文件 
using namespace std;
int b[10];
int  Nextprime(int a[],int len)
{
    int i=0;
    int j=len-2,k=len-1;
    int r=len-1,s=0;
    while(a[j]>a[j+1])
    {
        j--;
        if (j==-1) return 0;
    }
    while (a[j]>a[k]) k--;
    swap(a[j],a[k]);
    s=j+1;
    while (r>s)
    {
        swap(a[r],a[s]);
        r--;
        s++;
    }
    return 1;
}
void print(int a[],int len)
{
    for (int i=0;i<len;i++)
    {
        cout<<setw(5)<<a[i];
    }
}
int main()
{
    int n;
    cin>>n;
    for (int i=0;i<n;i++)
    {
        b[i]=i+1;
    }
    print(b,n);
    cout<<endl;
    while (Nextprime(b,n))
    {
        print(b,n);
        cout<<endl;
    }
    return 0;
}
