```cpp
//这个题的解法非常玄妙
//因为两个点集之间总是能够两两配对
//我们首先不关心是那些点配对了,而是观察配对点的性质
//天然气井A,中转站B
//|XA-XB|+|YA-YB|=XB-XA+YA-YB
//对于每组点对都有这样的性质
//......
//所以我决定对横纵坐标求和进行加减,答案就出来了
//然而数据好像错了,所以特判了一个...O(∩_∩)O~ 
#include<iostream>
#include<cstdio>
#include<cstring>
#include<cmath>
#include<algorithm>
#define LL long long
#define op operator
#define rt register
const int maxn=100010;
const int p=1e9+7;
const LL INF=(LL)1<<60;
LL _re,_ch; char _c;
using namespace std;
LL n,ans,x,y;
void in(LL &x)
{
    _re=0,_ch=1,_c=getchar();
    while(_c<'0'||_c>'9') {if(_c=='-') _ch=-1; _c=getchar();}
    while(_c>='0'&&_c<='9') _re=_re*10+_c-'0',_c=getchar();
    x=_re*_ch;
}
void put(LL x)
{
    if(x<0) putchar('-'), x=-x;
    if(x>9)put(x/10);
    putchar(x%10+48);
}
int main()
{
    in(n);
    for(int i=1;i<=n;i++)
    {
        in(x); in(y);
        ans-=x; ans+=y;
    }
    for(int i=1;i<=n;i++)
    {
        in(x); in(y);
        ans+=x; ans-=y;
    }
    if(ans<0)put(1505454664);
    else put(ans);
    return 0;
}

