struct node
{
	int t;
	int w;
};
deque<node> q;
int main()
{
	int n,m;
	cin >> n >> m;
	int ans = 0;
	int x;
	int M = 0;
	for(register int i = 1;i <= n;++ i)
	{
		while(!q.empty() &&  i - q.front().t > m)
			q.pop_front();
		cin >> x;
		ans += x;
		while(!q.empty() && q.back().w > ans)
			q.pop_back();
		q.push_back((node){i,ans});
		M = max(ans - q.front().w,M);
	}
	cout << M << endl;
}
