var
  i,n:longint;
  a:array[1..1000] of longint;
procedure sort(l,r:longint);//快速排序
var
  i,j,x,y:longint;
begin
  i:=l;
  j:=r;
  x:=a[(l+r) div 2];
  repeat
    while a[i]<x do
      inc(i);
    while x<a[j] do
      dec(j);
    if not(i>j) then
    begin
      y:=a[i];
      a[i]:=a[j];
      a[j]:=y;
      inc(i);
      j:=j-1;
    end;
  until i>j;
  if l<j then sort(l,j);
  if i<r then sort(i,r);
end;
begin
  readln(n);
  for i:=1 to n do
    readln(a[i]);
  sort(1,n);
  for i:=1 to n do
    if i mod 2=0 then writeln(a[i div 2])
    else writeln(a[n-i div 2]);
end.
```

