int opt[30][10000],i,j,k,f[30],d[30],t[30],n,h,ans=-1;
int main(){
    cin>>n>>h;
    h*=12;
    for(i=1;i<=n;++i)
        cin>>f[i];
    for(i=1;i<=n;++i)
        cin>>d[i];
    for(i=1;i<n;++i)
        cin>>t[i];
    memset(opt,-1,sizeof(opt));
    opt[0][0]=0;
    for(i=1;i<=n;++i)
        for(j=1;j<=h;++j){
            for(k=0;k<=j-t[i-1];++k)
                if((k-1)*d[i]<f[i]&&opt[i-1][j-t[i-1]-k]!=-1){
                    opt[i][j]=max(opt[i][j],opt[i-1][j-t[i-1]-k]+k*f[i]-k*(k-1)/2*d[i]);
                    ans=max(ans,opt[i][j]);
                }
        }
    cout<<ans<<endl;
    return 0;
}
```

