	for(int i=1;i<=n;i++)   //^此时以排除路程的时间影响
	{
                t1=t[i];
		init();     //初值
		int j=i;         
		while(1)    //枚举终点
		{
			bj=find(j);   //找到当前f最大值
			if(b[bj]<=0) break;//鱼钓没了
			sum=sum+b[bj];
			b[bj]-=a[bj].d;
			t1+=5;        //时间更新
