int n,m,ans;
inline int read()
{
	int s=0,w=1;
	char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-')w=-1;ch=getchar();}
	while(ch>='0' && ch<='9')s=(s<<3)+(s<<1)+(ch^48),ch=getchar();
	return s*w;
}
int main()
{
	//freopen("in.txt","r",stdin);
	n=read(),m=read()*60;
	for(int i=1;i<=n;++i)
	a[i]=read();
	for(int i=1;i<=n;++i)
	d[i]=read();
	for(int i=1;i<n;++i)
	dis[i]=read()*5;
	for(int i=1;i<=n;++i)
	for(int j=0;j<=m;++j)
	f[i][j]=-inf;
	f[1][0]=0;
	for(int i=1;i<=n;++i)
	{
		for(int j=m;j>=dis[i-1];--j)
		{
			f[i][j]=f[i-1][j-dis[i-1]];
			int tmp=0;
			for(int k=5;k<=j;k+=5)
			{
				if(j-dis[i-1]-k<0)break;
				f[i][j]=max(f[i][j],(f[i-1][j-dis[i-1]-k]+a[i]*(k/5)-tmp));
				tmp+=(k/5)*d[i];
			}
		}
	}
	for(int i=1;i<=n;++i)
	for(int j=0;j<=m;++j)
	ans=max(ans,f[i][j]);
