void cd(int ii,int ji){
	for(int i=ii;i<=ji;i++){
		q.push(e[i]);
	}
}
int main(){
	scanf("%d%d",&n,&h);
	h*=12;
	for(int i=1;i<=n;i++){
		scanf("%d",&e[i].f);
		e[i].id=i;
