	for(int i=1;i<=n;i++) scanf("%d",&e[i].d);
	for(int i=2;i<=n;i++){
		scanf("%d",&e[i].t);
		e[i].t+=e[i-1].t;
	}
	for(int j=1;j<=n;j++){
		int flag=h;
		flag-=e[j].t;
		cd(1,j);
		while(!q.empty()){
			if(flag-1<0){
				q.pop();
				continue;
			}
			else{
				flag--;
				node m=q.top();
				q.pop();
				sum+=m.f;
				if(m.f-m.d>0){
					m.f-=m.d;
					q.push(m);
				}
			}
		}
		ans=max(sum,ans);
		sum=0;
	}
	cout<<ans;
	return 0;
}
