template<typename T>
struct Priority_Queue {
	T heap[100005];
	int heap_size=0;
	T top() {
		return heap[1]; 
	}
	bool empty() {
		return heap_size==0;
	}
	int size() {
		return heap_size;
	}
	void push(T x) {
		heap_size++;
		heap[heap_size]=x;
		int now=heap_size;
		while(now>1) {
			int next=now>>1;
			if(heap[now]<heap[next]) {
				swap(heap[now],heap[next]);
			}
			else return;
			now=next;
		}
		return;
	}
	void pop() {
		heap[1]=heap[heap_size];
		heap_size--;
		int now=1,next;
		while((now<<1)<=heap_size) {
			next=now<<1;
			if(next+1<=heap_size&&heap[next+1]<heap[next]) next++;
			if(heap[next]<heap[now]) {
				swap(heap[now],heap[next]);
			}
			else return;
			now=next;
		}
		return;
	}
	inline void clear() {
		while(!empty()) pop();
	}
};
