int fr(){
	char ch=getchar();
	while(ch>'9'||ch<'0')
		ch=getchar();
	int sum=ch-'0';
	while((ch=getchar())>='0'&&ch<='9')
		sum=(sum<<3)+(sum<<1)+ch-'0';
	return sum;
}//快读（题目没有负数）
int ls(int p){
	return p<<1;
}
int rs(int p){
	return p<<1|1;
}
void push_up(int p){
	if(t[ls(p)].d>t[rs(p)].d)
		t[p]=t[ls(p)];
	else
		t[p]=t[rs(p)];
}//维护
void build(int p,int l,int r){
	if(l==r){
		t[p].d=f[l];
		t[p].k=l;
		return;
	}
	int mid=(l+r)>>1;
	build(ls(p),l,mid);
	build(rs(p),mid+1,r);
	push_up(p);
}//建树
void update(int p,int l,int r,int x,int y){
	if(l==x&&r==x){
		t[p].d=y;
		return;
	}
	int mid=(l+r)>>1;
	if(x<=mid)
		update(ls(p),l,mid,x,y);
	else
		update(rs(p),mid+1,r,x,y);
	push_up(p);
}//更新（将x鱼塘的鱼数更新为y）
int query(int p,int l,int r,int nl,int nr){
	if(nl>=l&&nr<=r)
		return p;
	int x,ans,sum=-1e6,mid=(nl+nr)>>1;
	if(l<=mid){
		x=query(ls(p),l,r,nl,mid);
		if(t[x].d>=sum){
			sum=t[x].d;
			ans=x;
		}
	}
	if(r>mid){
		x=query(rs(p),l,r,mid+1,nr);
		if(t[x].d>=sum){
			sum=t[x].d;
			ans=x;
		}
	}
	return ans;
}//查询（注意返回的是线段树的下标）
int main(){
