	for(int i=2;i<=n;i++){
		t1[i]=fr();
		t1[i]*=5;
		t1[i]+=t1[i-1];
	}//转化为分钟存储
	build(1,1,n);
	int x,s,u;
	for(int i=1;i<=n;i++){
		x=h*60;
		x-=t1[i];
		s=0;
		for(int j=1;j<i;j++)
			update(1,1,n,j,f[j]);//恢复初始状态
		while(1){
			if(x<5)//如果时间不够，break
				break;
			u=query(1,1,i,1,n);
			if(t[u].d<=0)
				break;//如果最多的鱼也不是正数，break
			s+=t[u].d;
			x-=5;
			update(1,1,n,t[u].k,t[u].d-d1[t[u].k]);
		}
		ans=max(ans,s);//ans取最大值
	}
