#define LL long long
using namespace std;
LL a[MAXN][MAXN];
//存储输入量的数组
LL s[MAXN][MAXN];
//二维前缀和数组
int main()
{
	ios::sync_with_stdio(0);
    //优化输入输出（仅限cin和cout）
