    class complex{
        public:
            long double r,v;
            complex(long double r_=0){r=r_;v=0;}
            complex(long double r_,long double v_){r=r_;v=v_;}
            complex operator+(const complex &c)const{return complex(r+c.r,v+c.v);}
            complex &operator+=(const complex &c){return *this=*this+c;}
            complex operator-(const complex &c)const{return complex(r-c.r,v-c.v);}
            complex &operator-=(const complex &c){return *this=*this-c;}
