const int maxn(1010);
template <typename T>
T max(T a,T b){
	return a>b?a:b;
}

int pri[maxn],cntpri;
int minpri[maxn];
void sieve(int n){
	for(int i(2);i<=n;++i){
		if(!minpri[i]){
			pri[++cntpri]=i;
			minpri[i]=i;
		}
		for(int j(1);j<=cntpri&&i*pri[j]<=n&&pri[j]<=minpri[i];++j){
			pri[i*pri[j]]=pri[j];
		}
	}
}

int w[maxn];
int dp[maxn];
#include<cstdio>
int main(){
	int s;
	scanf("%d",&s);
	sieve(s);
	for(int i(1);i<=s;++i){
		w[i]=1;
		int temp(i);
		for(int j(1);j<=cntpri&&pri[j]<=temp;++j){
			int now(1),ttemp(1);
			while(temp%pri[j]==0){
				now*=pri[j];
				temp/=pri[j];
				ttemp+=now;
			}
			w[i]*=ttemp;
		}
	}
    for(int i(1);i<=s;++i){
    	w[i]-=i;
    }
	for(int i(1);i<=s;++i){
		for(int j(s);j>=i;--j){
			dp[j]=max(dp[j],dp[j-i]+w[i]);
		}
	}
	printf("%d",dp[s]);
	return 0;
}
```

