int f[1000001],w[1001],c[1001];
int factor(int n){
    int i,s=0;
    for(i=1;i<n;i++){
        if(n%i==0)s+=i;//约数和函数
    }
    return s;
}
int main(){
    int n;
    cin>>n;
    for(int i=1;i<=n;i++){
        w[i]=i;//重量=他本身
        c[i]=factor(i);//价值=约数和
    }
    for(int i=1;i<=n;i++){
        for(int j=n;j>=w[i];j--){
            f[j]=max(f[j],f[j-w[i]]+c[i]);//背包模版
        }
    }
    cout<<f[n];//华丽丽地结束
    return 0;
}
