```cpp
#include <bits/stdc++.h>
#define ll long long
using namespace std;
const int maxn = 1300;
const int maxm = 0x3f3f3f3f;
int n, m;
char arr[maxn][maxn];
bool vis[maxn][maxn];
bool jud(int x, int y) {
	if (x <= 0 || x > n) return 0;
	if (y <= 0 || y > n) return 0;
	if (arr[x][y] == '*') return 0;
	return 1;
}
int get(int x, int y) {
	return (x - 1) * n + y;
}
int dx1[5] = {0, 0, 1, -1}, dy1[5] = {1, -1, 0, 0};
int dx2[5] = {0, 0, 2, -2}, dy2[5] = {2, -2, 0, 0};
int dx3[5] = {1, 1, -1, -1}, dy3[5] = {-1, 1, -1, 1};
int dis[maxn * maxn], ind[maxn * maxn];
struct node {
	int x, y;
}tmp;
inline void spfa() {
	queue <node> q;
	memset(dis, 0x3f, sizeof(dis));
	memset(ind, 0, sizeof(ind));
	dis[get(1, 1)] = dis[get(1, n)] = dis[get(n, 1)] = 1;
	ind[get(1, 1)] = ind[get(1, n)] = ind[get(n, 1)] = 1;
	tmp.x = 1, tmp.y = 1, q.push(tmp);
	tmp.x = 1, tmp.y = n, q.push(tmp);
	tmp.x = n, tmp.y = 1, q.push(tmp);
	while (!q.empty()) {
		node tmp = q.front(); q.pop();
		int x = tmp.x, y = tmp.y;
		if (arr[x][y] == 'A') 
			for (int i = 0; i < 4; i++) {
				int xx = dx1[i] + x;
				int yy = dy1[i] + y;
				if (!jud(xx, yy)) continue;
				if (dis[get(xx, yy)] > dis[get(x, y)] + 1) {
					dis[get(xx, yy)] = dis[get(x, y)] + 1;
					if (!ind[get(xx, yy)]) 
						q.push((node){xx, yy}), ind[get(xx, yy)] = 1;
				}	
			}
		if (arr[x][y] == 'B') 
			for (int i = 0; i < 4; i++) {
				int xx = dx2[i] + x;
				int yy = dy2[i] + y;
				if (!jud(xx, yy)) continue;
				if (dis[get(xx, yy)] > dis[get(x, y)] + 1) {
					dis[get(xx, yy)] = dis[get(x, y)] + 1;
					if (!ind[get(xx, yy)]) 
						q.push((node){xx, yy}), ind[get(xx, yy)] = 1;
				}	
			}

		if (arr[x][y] == 'C') 
			for (int i = 0; i < 4; i++) {
				int xx = dx3[i] + x;
				int yy = dy3[i] + y;
				if (!jud(xx, yy)) continue;
				if (dis[get(xx, yy)] > dis[get(x, y)] + 2) {
					dis[get(xx, yy)] = dis[get(x, y)] + 2;
					if (!ind[get(xx, yy)]) 
						q.push((node){xx, yy}), ind[get(xx, yy)] = 1;
				}	
			}
	}
}
int main() {
	ios::sync_with_stdio(false);
	cin >> n;

	for (int i = 1; i <= n; i++) 
	for (int j = 1; j <= n; j++)
		cin >> arr[i][j];
	if ((arr[1][1] == '*' && arr[1][n] == '*'&& arr[n][1] == '*') || arr[n][n] == '*') {
		cout << "No answer" << endl;
		return 0;
	}
	spfa();
	if (dis[get(n, n)] == maxm) cout << "No answer" << endl;
	else cout << dis[get(n, n)] << endl;
	return 0;
}
