    int t = n + m;
    int ans(0);
    for(int i(2);i <= t;++i)//枚举x+y的值
    {
        int now;
        int lastx,lasty;
        int j(1);//j代表枚举y的值
        while(i > j + n)++j;
        while(j < i && j <= m)
        {
            while(j < i && board[i-j][j] == 0 && j <= m && i <= j + n)++j;
            if(j >= i || j > m)break;//超出范围就跳出
            lastx = i - j;
            lasty = j;
