    for(int i=1;i<=n;i++)
    {
        for(int j=1;j<=m;j++)
        {
            int kuan=0;
            while(1)//向右扩展
            {
                kuan++;//枚举边长
                int sum1=2;//判断变量
                if(kuan+i-1>n||kuan+j-1>m)
                break;
                if(sum[i+kuan-1][j+kuan-1]-sum[i+kuan-1][j-1]-sum[i-1][j-1+kuan]+sum[i-1][j-1]==kuan)
                {
