```cpp
#include<cstdio>
#include<iostream>
#include<cstring>
using namespace std;
void print2();
struct creative_fish
{
    unsigned short v;
    unsigned short s;
};
struct creative_fish A[2501][2501];
unsigned short dt[2501][2501];
unsigned short ydt[2501][2501];
unsigned short n,m;
unsigned short ans=0;
void print1();
int read()
{
    int rt=0,fl=1;char ch=getchar();
    while(ch<'0'||ch>'9'){if(ch=='-')fl=-1;ch=getchar();}
    while(ch>='0'&&ch<='9'){rt=rt*10+ch-'0';ch=getchar();}
    return rt*fl;
}
void dp1()
{
    for(int i=1;i<=n;i++)
    {
        for(int j=1;j<=m;j++)
        {
            if(!ydt[i][j])
            {
                A[i][j].v=0;
                A[i][j].s=i;
            }
            if(ydt[i][j]&&!ydt[i-1][j-1]&&ydt[i-1][j]+ydt[i][j-1]==0)
            {
                A[i][j].v=1;
                A[i][j].s=i-1;
                if(A[i][j].v>ans) ans=A[i][j].v;
                continue ;
            }
            int tt=A[i-1][j-1].s;
            int ll=A[i-1][j-1].v;
            while(tt!=i)
            {
                if(ydt[i][j]&&(dt[i][j]-dt[i][j-(i-tt)]-dt[tt][j]+dt[tt][j-(i-tt)])==(i-tt))
                {
                    A[i][j].v=ll+1;
                    A[i][j].s=tt;
                    break;
                }
                tt++;
                ll--;
            }
            if(A[i][j].v>ans) ans=A[i][j].v;
        }
    }
}
void dp2()
{
    for(int i=1;i<=n;i++)
    {
        for(int j=m;j>=1;j--)//求出另一个方向的前缀和和一些初始化 
        {
            dt[i][j]=ydt[i][j];
            dt[i][j]+=dt[i-1][j]+dt[i][j+1]-dt[i-1][j+1];
            A[i][j].v=0;
            if(j==m) 
            {
                A[i][j+1].v=0;
                A[i][j+1].s=i;
            }
            if(ydt[i][j]) A[i][j].v=1;
            A[i][j].s=i-1;
        }
    }
    for(int i=1;i<=n;i++)
    {
        for(int j=m;j>=1;j--)
        {
            if(!ydt[i][j])
            {
                A[i][j].v=0;
                A[i][j].s=i;
            }
            if(ydt[i][j]&&!ydt[i-1][j+1]&&ydt[i-1][j]+ydt[i][j+1]==0)
            {
                A[i][j].v=1;
                A[i][j].s=i-1;
                if(A[i][j].v>ans) ans=A[i][j].v;
                continue ;
            }
            int tt=A[i-1][j+1].s;
            int ll=A[i-1][j+1].v;
            while(tt!=i)
            {
                if(ydt[i][j]&&dt[i][j]-dt[tt][j]-dt[i][j+(i-tt)]+dt[tt][j+(i-tt)]==(i-tt))
                {
                    A[i][j].v=ll+1;
                    A[i][j].s=tt;
                    break;
                }
                ll--;
                tt++;
            }
            if(A[i][j].v>ans) ans=A[i][j].v;
        }
    }
}
int main()
{
    n=read();
    m=read();
    for(int i=1;i<=n;i++)
    {
        for(int j=1;j<=m;j++)//求出左上到右下的前缀和以及一些初始化 
        {
            dt[i][j]=read();
            ydt[i][j]=dt[i][j];
            dt[i][j]+=dt[i-1][j]+dt[i][j-1]-dt[i-1][j-1];
            if(j==1)        //一些初始化 
            {    
                A[i][j-1].v=0;        
                A[i][j-1].s=i;
            }
            if(ydt[i][j]) A[i][j].v=1;
            A[i][j].s=i-1;
        }
    }
    dp1();
    dp2();
    printf("%d",ans);
    return 0;
}
```


