	  		for(int k=1;k<f[i-1][j+1].num2+1;k++)
	  		{
	  			if(f[i-k][j].mp==1 || f[i][j+k].mp==1)
	  			{
	  				f[i][j].num2=1;
	  				break;
				}
			}
			ans=max(ans,f[i][j].num2);
		}
	  }
