#define REP(i,n) for(int i=1;i<=n;i++)
#define PER(i,n) for(int i=n;i>=1;i--)
#define CLEAR(x) memset(x,0,sizeof(x))
using namespace std;
const int MAXN=2500;
int N,M,ans,opt[MAXN+10][MAXN+10][2],a1[MAXN+10],b1[MAXN+10],a2[MAXN+10],b2[MAXN+10];
