            f[i][j]=1;
            for(int k=f[i-1][j-1];k>=0;k--)
            {
                int sum = getRec(i-k,j-k,i,j);
                if(sum == k+1)
                {
                    f[i][j] = sum;
                    break;
                }
            }
            ans = max(f[i][j],ans);
        }
    }
    for(int i=1;i<=n;i++)
    {
        for(int j=m;j>=1;j--)
        {
            if(mp[i][j]==0)continue;
            f[i][j] = 1;
            for(int k=f[i-1][j+1];k>=0;k--)
            {
                int sum = getRec(i-k,j,i,j+k);
                if(sum == k+1)
                {
                    f[i][j] = sum;
                    break;
                }
            }
            ans = max(f[i][j],ans);
        }
    }
    printf("%d ",ans);
    return 0;
}
