inline bool read(){c=getchar();while(c<'0'||c>'1') c=getchar();p=c-'0';return p;}
inline bool pa(int x,int y,bool h,int p)
{
    for(int i=x-p;i<x;i++) if(ma[i][y]) return 0;
    if(!h) {for(int i=y-p;i<y;i++) if(ma[x][i]) return 0;}
