inline void se(int x,int y,bool h,int p)
{
    if (p<0) return;
    if(pa(x,y,h,p)) dp[x][y][h]=p+1;
    else {se(x,y,h,p-1);return;}
    if(Max<dp[x][y][h]) Max=dp[x][y][h];
