#include<cstdio>
#include<queue>
using namespace std;

queue<char>a;
int left,right,t;

inline void read(){
	char ch=getchar();
	while(ch!='@')a.push(ch),ch=getchar();
}

int main(){
	read();
	while(!a.empty()){
		if(a.front()=='('){
			++left;
			++t;
		}
		if(a.front()==')'){  
			++right;
			if(t)--t;
		}
		a.pop();
	}
	if(left==right&&t==0)printf("YES");
	 else printf("NO");
	return 0;
}
