    */ 
    for(int i=0;i<lo;i++)
    {
        if(a[i]=='(')//是左半括就入假栈 
            top++; 
        if(a[i]==')')//是右半括就出假栈 
        {
            if(top==0)//判断栈空 
            {
                printf("NO\n");
                return 0;
            }
            else
                top--;//出栈 
        }
    }
    if(top!=0)//判断栈空 
        printf("NO");
    return 0;
}
```

