```cpp
#include <iostream>
#include <algorithm>
using namespace std;

class Stack{
public:
    char *data;
    int length;
    Stack(int n){
        data = new char[n];
        length = 0;
    }
    ~Stack(){
        delete data;
    }
    void push(char chr){
        for(int i=length-1;i>0;i--){
            data[i+1] = data[i];
        }
        data[0] = chr;
        length++;
    }
    void pop(){
        if(isempty()) return;
        for(int i=0;i<length;i++){
            data[i] = data[i+1];
        }
        length--;
    }
    char top(){
        if(length != 0)
            return data[0];
    }
    bool isempty(){
        if(length == 0){
            return true;
        }else{
            return false;
        }
    }

};

int main(){
    Stack *mystack = new Stack(300);
    char tmp;
    while(1){
        cin>>tmp;
        if(tmp == '@'){
            break;
        }
        if(tmp == '('){
            mystack->push('(');
        }
        if(tmp == ')'){
            if(mystack->isempty()){
                cout<<"NO"<<endl;
                return 0;
            }
            mystack->pop();
        }
    }
    if(mystack->isempty()){
        cout<<"YES"<<endl;
    }else{
        cout<<"NO"<<endl;
    }
    return 0;
}
```
