```cpp
#include<iostream>
#include<cstdio>
#include<cstdlib>
#include<algorithm>
#include<cmath>
#include<cstring>
#include<stack>
using namespace std;
char ch[256];
stack<char>epstk;
bool IsLeft(char c)
{
    return c=='(';
}
bool IsRight(char c)
{
    return c==')';
}
bool IsMathBrackets()
{
    for(int i=1;i<=strlen(ch+1);i++)
    {
        if(IsLeft(ch[i]))
        {
            epstk.push(ch[i]);
        }
        if(IsRight(ch[i]))
        {
            if(epstk.empty())
            {
                return false;
            }
            epstk.pop();
        }
    }
    return epstk.empty();
}
int main()
{
    scanf("%s",ch+1);      //从第一位开始读入字符数组。
    if(IsMathBrackets())
    {
        puts("YES");
    }
    else
    {
        puts("NO");
    } 
    return 0;
}

```

